package basic;

public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1= 5;
		num1+=5;
		System.out.println("The assignment operator for addition is "+num1);
		
		int num2=5;
		num2-=2;
		System.out.println("The assignment operator for subtraction is "+num2);
		
		int num3=6;
		num3*=10; 
		System.out.println("The assignment operator value for multiplication is "+num3);

		int num4=10;
		num4/=2;
		System.out.println("The assignment operator value for division is "+num4);
		
		int num5=5;
		num5%=5; 
		System.out.println("The assignment operator value for modulo is "+num5);
		
	}

	}


