package basic;

public class DefaultValuesofArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

        byte byteDefault = 0;
        short shortDefault = 0;
        int intDefault = 0;
        long longDefault = 0;
        float floatDefault = 0;
        double doubleDefault = 0;
        char charDefault = 0;
        boolean booleanDefault = false;

        // Default values for reference data types (objects)
        String stringDefault = null;

        // Print default values
        System.out.println("Default values for primitive data types:");
        System.out.println("byteDefault: " + byteDefault);
        System.out.println("shortDefault: " + shortDefault);
        System.out.println("intDefault: " + intDefault);
        System.out.println("longDefault: " + longDefault);
        System.out.println("floatDefault: " + floatDefault);
        System.out.println("doubleDefault: " + doubleDefault);
        System.out.println("charDefault: " + charDefault);
        System.out.println("booleanDefault: " + booleanDefault);

        System.out.println("\nDefault value for reference data type (String):");
        System.out.println("stringDefault: " + stringDefault);
    }

}
