package basic;

public class Marks {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int marks = 76;
		
		if (marks > 90) {
            System.out.println("A grade");
            
        } else if (marks > 75) {
            System.out.println("B grade");
            
        } else if (marks > 50) {
            System.out.println("C grade");
            
        } else {
            System.out.println("Fail");
        }
        
        
	}

}
